{
  "pattern": "cookbook_entry_metadata",
  "fields": {
    "language": "python|javascript|typescript|html|json|text|auto",
    "extraction_strategy": "python_function|js_function_regex|html_markers|raw_snippet",
    "locator": {
      "file_path": "absolute or project-relative",
      "function_name": "optional",
      "class_name": "optional",
      "start_marker": "optional",
      "end_marker": "optional"
    }
  },
  "notes": [
    "JS/TS extraction uses regex with brace balancing.",
    "HTML supports <script> extraction or marker-based.",
    "JSON/Text snippets stored directly."
  ]
}
